# Parse the HTML content
soup = BeautifulSoup(fixed_tariff_html, 'html.parser')

# Find all links that end with .pdf
pdf_links = soup.find_all('a', href=lambda href: href and href.lower().endswith('.pdf'))

# Function to sanitize filenames
def sanitize_filename(name):
    # Remove invalid characters and strip leading/trailing whitespace
    return "TILs/residential/credit/" + re.sub(r'[\\/*?:"<>|]', "", name).strip()

# Download each PDF
for link in pdf_links:
    href = link.get('href')
    link_text = link.get_text().strip()
    if not href:
        continue

    # Construct the full URL if necessary
    pdf_url = href if href.startswith('http') else f"https://uw.co.uk{href}"

    # Sanitize the link text to create a valid filename
    filename = sanitize_filename(link_text)
    if not filename.lower().endswith('.pdf'):
        filename += '.pdf'

    # Download and save the PDF
    print(f"Downloading: {pdf_url} as {filename}")
    pdf_response = requests.get(pdf_url)
    pdf_response.raise_for_status()
    with open(filename, 'wb') as f:
        f.write(pdf_response.content)

print("All PDFs have been downloaded.")
